import requests
import json
import sys
from datetime import datetime

# Base URL from environment
BASE_URL = "https://deploy-debug-hero.preview.emergentagent.com/api"

def test_endpoint(method, endpoint, data=None, headers=None, expected_status=None):
    """Test bir endpoint'i ve sonucu dÃ¶ndÃ¼r"""
    url = f"{BASE_URL}{endpoint}"
    
    try:
        if method.upper() == 'GET':
            response = requests.get(url, headers=headers, timeout=10)
        elif method.upper() == 'POST':
            response = requests.post(url, json=data, headers=headers, timeout=10)
        elif method.upper() == 'PUT':
            response = requests.put(url, json=data, headers=headers, timeout=10)
        elif method.upper() == 'DELETE':
            response = requests.delete(url, headers=headers, timeout=10)
        else:
            return {"error": f"Unsupported method: {method}"}
        
        result = {
            "method": method.upper(),
            "endpoint": endpoint,
            "status_code": response.status_code,
            "success": True,
            "response_time": response.elapsed.total_seconds()
        }
        
        # Response content'i parse etmeye Ã§alÄ±ÅŸ
        try:
            result["response"] = response.json()
        except:
            result["response"] = response.text[:200] if response.text else "No content"
        
        # Expected status kontrolÃ¼
        if expected_status and response.status_code != expected_status:
            result["warning"] = f"Expected {expected_status}, got {response.status_code}"
        
        return result
        
    except requests.exceptions.RequestException as e:
        return {
            "method": method.upper(),
            "endpoint": endpoint,
            "error": str(e),
            "success": False
        }

